import "./App.css";
import { useState } from "react";
import Axios from "axios";

import "bootstrap/dist/css/bootstrap.min.css";

//import Swal from 'sweetalert2'

function App() {
 
 

  

  const [noticiasList, setNoticias] = useState([]);


  const getNoticias = ()  => {
    Axios.get("http://localhost:3001/noticias").then((response) => {
      setNoticias(response.data);
    });
  }




  
 



  return (
    <div className="container">

      <div className="grid-cols-8	">
        <div>1</div>
        <div>2</div>
        <div>3</div>

      </div>





{/* ----------------tabla---------------- */}


 <button className="btn btn-dark" onClick={getNoticias}>
            Listar
          </button> 


              <table className="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">Edad</th>
                <th scope="col">Pais</th>
                <th scope="col">Puesto</th>


              </tr>
            </thead>
            <tbody>

            {noticiasList.map((val,key) => {
            return (
            <tr key={val.id}>
            <th >{val.id}</th>
            <td>{val.topic}</td>
            <td>{val.imagen}</td>
            <td>{val.fecha}</td>
            <td>{val.subject}</td>
            <td>


            </td>
            </tr>
            )
            })}

            </tbody>

            
        </table>

 
              {noticiasList.map((val,key) => {
          return(
            <img key={val.id} src={val.imagen} alt=""></img>
          )
        })}


    </div>
  );
}

export default App;
