import "./App.css";
import { useState } from "react";
import Axios from "axios";

import "bootstrap/dist/css/bootstrap.min.css";

//import Swal from 'sweetalert2'

function App() {
 
 

  

  const [noticiasList, setNoticias] = useState([]);


  const getNoticias = ()  => {
    Axios.get("http://localhost:3001/noticias").then((response) => {
      setNoticias(response.data);
    });
  }




  
 



  return (
    <div className="container">

{noticiasList.map((val,key) => {
          return(
            <div className="">
            {/* <img key={val.id} src={val.imagen} alt=""></img> */}

              
      <div className="card" style={{width: '18rem'}}>
          <img key={val.id} src={val.imagen} className="card-img-top" alt="..."/>
        <div className="card-body">
          <h5 className="card-title">{val.topic}</h5>
          <p className="card-text">{val.subject}</p>
          <p className="card-text">{val.fecha}</p>
        <a href="#" className="dark">Mas informacion</a>
        </div>
      </div>


            </div>
          )})}


      





{/* ----------------tabla---------------- */}


 <button className="btn btn-dark" onClick={getNoticias}>
            Listar
          </button> 


              <table className="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">Edad</th>
                <th scope="col">Pais</th>
                <th scope="col">Puesto</th>


              </tr>
            </thead>
            <tbody>

            {noticiasList.map((val,key) => {
            return (
            <tr key={val.id}>
            <th >{val.id}</th>
            <td>{val.topic}</td>
            <td>{val.imagen}</td>
            <td>{val.fecha}</td>
            <td>{val.subject}</td>
            <td>


            </td>
            </tr>
            )
            })}

            </tbody>

            
        </table>

 



    </div>
  );
}

export default App;
