import "./App.css";
import { useState } from "react";
import Axios from "axios";

import "bootstrap/dist/css/bootstrap.min.css";

//import Swal from 'sweetalert2'

function App() {
 
 

  

  const [noticiasList, setNoticias] = useState([]);


  const getNoticias = ()  => {
    Axios.get("http://localhost:3001/noticias").then((response) => {
      setNoticias(response.data);
    });
  }




  
 



  return (
<div className="background">
    <div className="container">
            
        <nav class="navbar  border-bottom border-body navbar-expand-lg">
          <div class="container-fluid">
            <a class="navbar-brand navText" onClick={getNoticias}>NOTICIAS STORM</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarSupportedContent">
              <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                <li class="nav-item">
                  <a class="nav-link active navText" aria-current="page" href="#">Home</a>
                </li>

              </ul>
              <form class="d-flex me-auto, mb-2, mb-lg-2, me-4" role="search">
                <input class="form-control me-2" type="search" placeholder="Buscar" aria-label="Search"/>
                <button class="btn btn-outline-light" type="submit">Buscar</button>
              </form>
            </div>
          </div>
        </nav>












      


{noticiasList.map((val,key) => {
          return(
            <div className="contenedores">
            <div className="row">
            {/* <img key={val.id} src={val.imagen} alt=""></img> */}

    <div className="col-xl-6 col-sm-2">
      <div className="card" style={{width: '20rem'}}>
          <img key={val.id} src={val.imagen} className="card-img-top" alt="avatar"/>
        <div className="card-body">
          <h5 className="card-title">{val.topic}</h5>
          <p className="card-text">{val.subject}</p>
          <p className="card-text">{val.fecha}</p>
        <a href="#" className="btn btn-dark">Mas informacion</a>
        </div>
      </div>

          </div>
            </div>
            </div>
          )})}





{/* ----------------tabla---------------- */}


 {/* <button className="btn btn-dark" onClick={getNoticias}>
            Listar
          </button>  */}


        
 



    </div>
</div>
  );
}

export default App;
