import "./App.css";
import { useState } from "react";
import Axios from "axios";

import "bootstrap/dist/css/bootstrap.min.css";

//import Swal from 'sweetalert2'

function App() {
 
 

  

  const [noticiasList, setNoticias] = useState([]);


  const getNoticias = ()  => {
    Axios.get("http://localhost:3001/noticias").then((response) => {
      setNoticias(response.data);
    });
  }




  
 



  return (
    <div className="container">

      <div className="card">

      <div className="card" style={{width: '18rem'}}>
          <img src="..." className="card-img-top" alt="..."/>
        <div className="card-body">
          <h5 className="card-title">Card title</h5>
          <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <a href="#" className="btn btn-primary">Go somewhere</a>
        </div>
      </div>

      </div>





{/* ----------------tabla---------------- */}


 <button className="btn btn-dark" onClick={getNoticias}>
            Listar
          </button> 


              <table className="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">Edad</th>
                <th scope="col">Pais</th>
                <th scope="col">Puesto</th>


              </tr>
            </thead>
            <tbody>

            {noticiasList.map((val,key) => {
            return (
            <tr key={val.id}>
            <th >{val.id}</th>
            <td>{val.topic}</td>
            <td>{val.imagen}</td>
            <td>{val.fecha}</td>
            <td>{val.subject}</td>
            <td>


            </td>
            </tr>
            )
            })}

            </tbody>

            
        </table>

 
              {noticiasList.map((val,key) => {
          return(
            <img key={val.id} src={val.imagen} alt=""></img>
          )
        })}


    </div>
  );
}

export default App;
